#include <iostream>#include <cstdio>#include <cstring>#include <cstdlib>#include <string>#include <cmath>#include <vector>#include <string>#include <sstream>#include <map>#include <algorithm>using namespace std;int T;int n;char ch;bool fst = true;void sc(char x, int m) {    for (int i = 0; i < m; ++ i)        cout << x;}int main() {        cin >> T;    while (T --) {        cin >> ch >> n;        if (fst) fst = false;        else cout << endl;        int w = (n-3+1) / 2;        int l1 = n-3-w;        int l2 = w;        int kuan = 1 + n/6;        sc(' ', kuan);        sc(ch, w);        cout << endl;        for (int i = 0; i < l1; ++ i) {            sc(ch, kuan);            sc(' ', w);            sc(ch, kuan);            cout << endl;        }        sc(' ', kuan);        sc(ch, w);        cout << endl;        for (int i = 0; i < l2; ++ i) {            sc(ch, kuan);            sc(' ', w);            sc(ch, kuan);            cout << endl;        }        sc(' ', kuan);        sc(ch, w);        cout << endl;    }    return 0;}