#include <iostream>#include <cstdio>#include <cstring>#include <cstdlib>#include <string>#include <cmath>#include <vector>#include <string>#include <sstream>#include <map>#include <algorithm>using namespace std;vector < string > ans;string buf, b2;int t1, t2;bool check() {    b2 = buf;    reverse(b2.begin(), b2.end());    return (b2 == buf);}string Trans(string x) {    b2 = x;    reverse(b2.begin(), b2.end());    stringstream s1, s2, s3;    s1 << x;    s2 << b2;    s1 >> t1;    s2 >> t2;    t1 += t2;    s3 << t1;    s3 >> b2;    return b2;}int main() {        while (cin >> buf) {        ans.clear();        ans.push_back(buf);        while (!check()) {            buf = Trans(buf);            ans.push_back(buf);        }        cout << ans.size()-1 << endl;        cout << ans[0];        for (int i = 1; i < ans.size(); ++ i)            cout << "--->" << ans[i];        cout << endl;    }    return 0;}