#include <iostream>#include <cstdio>#include <cstring>#include <cstdlib>#include <string>#include <cmath>#include <vector>#include <string>#include <map>#include <algorithm>using namespace std;int M[8];int R[8];string s;int main() {        while (cin >> M[1] >> M[2]) {        cin >> s;        R[1] = R[2] = R[3] = 0;        for (int i = 0; i < s.size(); ++ i)            if (s[i] == 'A')                R[1] = M[1];            else if (s[i] == 'B')                R[2] = M[2];            else if (s[i] == 'C')                M[1] = R[3];            else if (s[i] == 'D')                M[2] = R[3];            else if (s[i] == 'E')                R[3] = R[1] + R[2];            else                 R[3] = R[1] - R[2];        printf("%d,%d\n", M[1], M[2]);    }    return 0;}