#include <iostream>#include <cstdio>#include <cstring>#include <cstdlib>#include <string>#include <cmath>#include <vector>#include <string>#include <sstream>#include <map>#include <algorithm>using namespace std;int n;int main() {        while (cin >> n) {        if (n == 0) break;        if (n == 1) {            printf("%3d\n", n);            continue;        }        for (int i = 1; i <= n; ++ i)            printf("%3d", i);        printf("\n");        for (int ed = 4*n-4, st = n+1, i = 2; i < n; -- ed, ++ st, ++ i) {            printf("%3d", ed);            for (int j = 2; j < n; ++ j)                printf("   ");            printf("%3d\n", st);        }        for (int i = 3*(n-1)+1; i > 2*(n-1); -- i)            printf("%3d", i);        printf("\n");    }    return 0;}